import { CheckboxOverrides } from "baseui/checkbox";
import { RadioOverrides } from "baseui/radio";
import { StyleObject } from "styletron-react";
import { Theme } from "../";

type StyleOverridesProps = StyleObject & { $theme: Theme };

export const tickOverrides = (
  theme: Theme
): CheckboxOverrides | RadioOverrides => ({
  Label: {
    style: ({ $disabled, $error }: StyleOverridesProps): StyleObject => {
      const { tickBorderDisabled, tickBorderError } = theme.colors;

      const defaultStyles = {
        fontFamily: "Public Sans",
        fontSize: "14px",
      };

      if ($disabled) {
        return { ...defaultStyles, color: tickBorderDisabled };
      }

      if ($error) {
        return { ...defaultStyles, color: tickBorderError };
      }

      return defaultStyles;
    },
  },
});

export const getTickBorderColor = (
  { $checked, $disabled, $error }: StyleOverridesProps,
  theme: Theme
): string => {
  const {
    tickBorder,
    tickBorderDisabled,
    tickBorderError,
    tickBorderSelected,
  } = theme.colors;

  if ($disabled) {
    return tickBorderDisabled;
  }

  if ($error) {
    return tickBorderError;
  }

  if ($checked) {
    return tickBorderSelected;
  }

  return tickBorder;
};
