import { SharedProps } from "baseui/input";
import { StyleObject } from "styletron-react";
import { Theme } from "../";

type RootColors = (
  $disabled: boolean,
  $isFocused: boolean,
  $error: boolean,
  $positive: boolean,
  $colors: Theme["colors"]
) => string;

const getBorderColor: RootColors = (
  $disabled,
  $isFocused,
  $error,
  $positive,
  colors
) => {
  if ($disabled) {
    return colors.borderStateDisabled;
  }

  if ($isFocused) {
    return colors.borderSelected;
  }

  if ($error) {
    return colors.inputBorderError;
  }

  if ($positive) {
    return colors.inputBorderPositive;
  }

  return colors.inputBorder;
};

export const inputOverrides =
  (theme: Theme) =>
  ({ $disabled, $error, $positive, $isFocused }: SharedProps): StyleObject => ({
    ...theme.borders.borderWidth100,
    borderColor: getBorderColor(
      $disabled,
      $isFocused,
      $error,
      $positive,
      theme.colors
    ),
  });
