import { FC } from 'react';
import styled from 'styled-components';
import { CryptoSymbols } from '../static/types/crypto';
import { IconSizes } from '../static/types/icons';
import { cryptoIcons } from '../static/images/icons/crypto';
import { iconSizes } from '../theme/sizes';

export interface CryptoIconProps {
  cryptoSymbol: CryptoSymbols;
  size?: IconSizes;
}

export const CryptoIcon: FC<CryptoIconProps> = ({ size = 'small', cryptoSymbol }) => {
  return (
    <StyledImage
      data-testid={`svg-icon-${cryptoSymbol}`}
      alt={cryptoSymbol}
      src={cryptoIcons[cryptoSymbol]}
      width={iconSizes(size)}
      height={iconSizes(size)}
    />
  );
};

const StyledImage = styled.img`
  /*
* Target IE to respect aspect ratios and sizes for img tags containing SVG files
*
* [1] IE9
* [2] IE10+
*/
  /* 1 */
  .ie9 img[src$='.svg'] {
    width: 100%;
  }
  /* 2 */
  @media screen and (-ms-high-contrast: active), (-ms-high-contrast: none) {
    img[src$='.svg'] {
      width: 100%;
    }
  }
`;
