import React, { FC, useState } from "react";
import { Icon } from "@iconify/react";
import styled from "styled-components";
import Image from "../../image/image";

interface CreditCardType {}

const CreditCard: FC<CreditCardType> = () => {
  const balance = "$20,000.00";
  const [showBalance, setShowBalance] = useState(false);
  return (
    <CardItemStyle>
      <div>
        <Title>Balance</Title>
        <BalanceWrapper onClick={() => setShowBalance(!showBalance)}>
          <div style={{ marginRight: "10px" }}>
            {showBalance ? balance : "*******"}
          </div>
          <Icon
            width={20}
            icon={showBalance ? "eva:eye-fill" : "eva:eye-off-fill"}
          />
        </BalanceWrapper>
      </div>
      <CardNumber>
        <img width={30} alt="credit-card" src="/assets/Mastercard-logo.svg" />
        <Info style={{ marginLeft: "10px" }}>**** **** **** 1234</Info>
      </CardNumber>
      <OtherDetails>
        <div>

        </div>
      </OtherDetails>
    </CardItemStyle>
  );
};

export default CreditCard;

const CardItemStyle = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: space-between;
  background-image: url("/assets/bg_card.png");
  background-size: cover;
  height: 200px;
  width: 350px;
  color: white;
  border-radius: 20px;
  padding: 20px;
  font-family: Public Sans;
`;

const Title = styled.div`
  color: #fffff;
  opacity: 0.7;
`;

const Subtitle = styled.div`
  color: #fffff;
  opacity: 0.7;
  font-weight: thin;
`;

const Info = styled.div`
  font-size: 20px;
`;

const BalanceWrapper = styled.div`
  display: flex;
  width: 100%;
  flex-direction: row;
  height: 20px;
  margin: 10px 0;
  font-size: 30px;
  :hover {
    cursor: pointer;
  }
`;

const CardNumber = styled.div`
  display: flex;
  flex-direction: row;
  margin-top: 40px;
  font-size: 25px;
  align-items: center;
`;

const OtherDetails = styled.div`
  display: flex;
  flex-direction: row;
  margin-top: 40px;
  font-size: 25px;
  align-items: center;
`;
