import React, { FC, useState } from "react";
import { Icon } from "@iconify/react";
import styled from "styled-components";

interface CreditCardType {}

const CreditCard: FC<CreditCardType> = () => {
  const balance = "$20,000.00";
  const [showBalance, setShowBalance] = useState(false);
  return (
    <CardItemStyle>
      <Subtitle>Balance</Subtitle>
      <BalanceWrapper onClick={() => setShowBalance(!showBalance)}>
        <div>{showBalance ? balance : "*******"}</div>
        <Icon siz icon={showBalance ? "eva:eye-fill" : "eva:eye-off-fill"} />
      </BalanceWrapper>
    </CardItemStyle>
  );
};

export default CreditCard;

const CardItemStyle = styled.div`
  display: flex;
  flex-direction: column;
  background-image: url("/assets/bg_card.png");
  background-size: cover;
  height: 200px;
  width: 350px;
  color: white;
  border-radius: 20px;
  padding: 20px;
  font-family: Public Sans;
`;

const Subtitle = styled.div`
  color: #fffff;
  opacity: 0.7;
`;

const BalanceWrapper = styled.div`
  display: flex;
  width: 100%;
  flex-direction: row;
  height: 20px;
  margin: 10px 0;
  font-size: 30px;
  :hover {
    cursor: pointer;
  }
`;
