import Table from "@mui/material/Table";
import TableContainer from "@mui/material/TableContainer";
import TableRow from "@mui/material/TableRow";
import TableBody from "@mui/material/TableBody";
import TableCell from "@mui/material/TableCell";
import styled from "styled-components";
import { FC } from "react";
import { sentenceCase } from "change-case";
import {
  Box,
  Button,
  Card,
  CardHeader,
  CardProps,
  colors,
  Divider,
  TableHead,
} from "@mui/material";
import TableHeadCustom from "./tableHeadCustom";
import { Icon as Iconify } from "@iconify/react";
import Scrollbar from "../../scroll";
import { fCurrency } from "../../utils/number";
import Label from "../label";
import palette from "../theme/palette";

export type RowProps = {
  id: string;
  category: string;
  price: number;
  status: string;
  date?: string;
};

interface Props extends CardProps {
  title?: string;
  subheader?: string;
  tableData: RowProps[];
  tableLabels: any;
}

export default function TableComponent({
  title,
  subheader,
  tableData,
  tableLabels,
  ...other
}: Props) {
  return (
    <Card {...other} style={{ backgroundColor: "#212B36", color: "#fff" }}>
      <CardHeader title={title} subheader={subheader} sx={{ mb: 3 }} />

      <TableContainer sx={{ minWidth: 720 }}>
        <Table
          style={{ backgroundColor: "#212B36", color: "#fff" }}
          sx={{
            MuiTableRow: {
              styleOverrides: {
                root: {
                  borderBottom: "none",
                  "&.Mui-selected": {
                    backgroundColor: palette.dark.action.selected,
                    "&:hover": {
                      backgroundColor: palette.dark.action.hover,
                    },
                  },
                },
              },
            },
            MuiTableCell: {
              styleOverrides: {
                root: {
                  borderBottom: "none",
                },
                head: {
                  color: palette.dark.text.secondary,
                  backgroundColor: palette.dark.background.neutral,
                  "&:first-of-type": {
                    paddingLeft: 3,
                    borderTopLeftRadius: 8,
                    borderBottomLeftRadius: 8,
                    boxShadow: `inset 8px 0 0 ${palette.dark.background.paper}`,
                  },
                  "&:last-of-type": {
                    paddingRight: 3,
                    borderTopRightRadius: 8,
                    borderBottomRightRadius: 8,
                    boxShadow: `inset -8px 0 0 ${palette.dark.background.paper}`,
                  },
                },
                stickyHeader: {
                  backgroundColor: palette.dark.background.paper,
                  backgroundImage: `linear-gradient(to bottom, ${palette.dark.background.neutral} 0%, ${palette.dark.background.neutral} 100%)`,
                },
                body: {
                  "&:first-of-type": {
                    paddingLeft: 3,
                  },
                  "&:last-of-type": {
                    paddingRight: 3,
                  },
                },
              },
            },
          }}
        >
          <TableHeadCustom headLabel={tableLabels} />

          <TableBody>
            {tableData.map((row) => (
              <Row key={row.id} row={row} />
            ))}
          </TableBody>
        </Table>
      </TableContainer>

      <Divider />

      <Box sx={{ p: 2, textAlign: "right" }}>
        <Button
          size="small"
          color="inherit"
          endIcon={<Iconify icon={"eva:arrow-ios-forward-fill"} />}
        >
          View All
        </Button>
      </Box>
    </Card>
  );
}

type TableRowProps = {
  row: RowProps;
};

const Row: FC<TableRowProps> = ({ row }) => {
  return (
    <TableRow style={{ color: "#FFF" }}>
      <TableCell style={{ color: "#FFF" }}>{`${row.date}`}</TableCell>

      <TableCell style={{ color: "#FFF" }}>{row.category}</TableCell>

      <TableCell style={{ color: "#FFF" }}>{fCurrency(row.price)}</TableCell>

      <TableCell style={{ color: "#FFF" }}>
        <Label
          variant="ghost"
          color={
            (row.status === "in_progress" && "info") ||
            (row.status === "failed" && "error") ||
            (row.status === "complete" && "success") ||
            "info"
          }
        >
          {sentenceCase(row.status)}
        </Label>
      </TableCell>

      <TableCell align="right"></TableCell>
    </TableRow>
  );
};

const Container = styled.div`
  color: white;
`;

const StyledTable = styled(Tab)
