import merge from "lodash/merge";
import React, { FC } from "react";
import ReactApexChart from "react-apexcharts";
import styled from "styled-components";
import { colors, legacyColors, ThemeColor } from "../../theme/colors";
import BaseOptionChart from "../../theme/charts";
import { fNumber } from "../../../utils/number";

export interface ChartProps {
  color?: ThemeColor;
  title?: string;
  subtitle?: string;
  chartLabels: string[];
  chartData: {
    label: string;
    value: number;
  }[];
  chartColors: string[];
  diameter: string;
}

const PieChart: FC<ChartProps> = ({
  diameter = "100%",
  chartData,
  chartColors,
}) => {
  const chartLabels = chartData.map((i) => i.label);
  const chartSeries = chartData.map((i) => i.value);
  console.log(chartSeries);
  const chartOptions = merge(BaseOptionChart(), {
    chart: {
      type: "donut",
      length: 3,
    },
    length: 3,
    colors: ["#FFFFF", "#FFFFF", "#FFFFF"],
    labels: chartLabels,
    stroke: { colors: [legacyColors.grey500] },
    legend: { floating: true, horizontalAlign: "center" },
    tooltip: {
      fillSeriesColor: false,
      y: {
        formatter: (seriesName: string) => fNumber(seriesName),
        title: {
          formatter: (seriesName: string) => `${seriesName}`,
        },
      },
    },
    plotOptions: {
      pie: {
        donut: {
          size: "90%",
          labels: {
            value: {
              formatter: (val: number | string) => fNumber(val),
            },
            total: {
              formatter: (w: { globals: { seriesTotals: number[] } }) => {
                const sum = w.globals.seriesTotals.reduce((a, b) => a + b, 0);
                return fNumber(sum);
              },
            },
          },
        },
      },
    },
  });

  return (
    <Container>
      <ReactApexChart
        type="donut"
        series={chartSeries}
        options={chartOptions}
        height={diameter}
        labels={["Apple", "Mango", "Orange", "Watermelon"]}
      />
    </Container>
  );
};

export default PieChart;

const Container = styled.div`
  color: white;
`;
