import merge from "lodash/merge";
import React, { FC } from "react";
import ReactApexChart from "react-apexcharts";
import styled from "styled-components";
import { colors, ThemeColor } from "../../theme/colors";
import BaseOptionChart from "../../theme/charts";
import { CryptoIcon } from "../../icon/icon";
import { CryptoSymbols } from "../../static/types";

export interface ChartProps {
  color?: ThemeColor;
  title?: string;
  subtitle?: string;
  chartLabels: string[];
  chartData: number[];
  width?: string;
  height?: string;
  name: string;
  currency?: CryptoSymbols;
}

const LineChart: FC<ChartProps> = ({
  color = "blue",
  width = "100%",
  height = "100%",
  chartLabels,
  chartData,
  name,
  currency,
}) => {
  const chartOptions = merge(BaseOptionChart(), {
    plotOptions:{},
    candlestick: {
      colors: {
        upward: "#3C90EB",
        downward: "#DF7D46",
      },
    },
  });

  return (
    <Container>
      <Title>{currency && <CryptoIcon cryptoSymbol={currency} />}</Title>{" "}
      <ReactApexChart
        type="candlestick"
        series={[
          {
            data: [
              [1538856000000, 6593.34, 6600, 6582.63, 6600],
              [1538856900000, 6595.16, 6604.76, 6590.73, 6593.86],
            ],
          },
        ]}
        options={chartOptions}
        height={height}
        width={width}
      />
    </Container>
  );
};

export default LineChart;

const Container = styled.div`
  color: white;
`;

const Title = styled.div`
  display: flex;
  margin: 0 0 20px 20px;
`;
