import React, { FunctionComponent } from "react";
import styled, { Keyframes, css, keyframes } from "styled-components";
import { useStyletron } from "../";

const LoaderWrapper = styled.div`
  display: flex;
`;
const StyledDot = styled.div<{ delay: string; dotColor: string }>`
  ${({ delay, dotColor }) => {
    const flashing = (): Keyframes => keyframes`
  from { opacity: 0%; filter: brightness(0.5); }
  to { opacity: 100%; filter: brightness(1); }
`;
    return css`
      width: 8px;
      height: 8px;
      border-radius: 50%;
      background-color: ${dotColor};
      animation: ${flashing} 1s infinite linear alternate;
      animation-delay: ${delay};
    `;
  }}
`;
export interface ButtonLoaderProps {
  leftColor: string;
  middleColor: string;
  rightColor: string;
}

export const StyledLoader: FunctionComponent<ButtonLoaderProps> = ({
  leftColor,
  middleColor,
  rightColor,
}) => {
  const [, theme] = useStyletron();
  const { animation, sizing } = theme;
  return (
    <LoaderWrapper aria-hidden="true">
      <StyledDot
        delay={animation.timing300}
        dotColor={leftColor}
        style={{ marginRight: sizing.scale100 }}
      />
      <StyledDot
        delay={animation.timing600}
        dotColor={middleColor}
        style={{ marginRight: sizing.scale100 }}
      />
      <StyledDot delay={animation.timing1000} dotColor={rightColor} />
    </LoaderWrapper>
  );
};
