import React, { FunctionComponent } from "react";
import { styled } from "baseui";
import colors from "../../colors";

const Wrapper = styled("div", {
  boxShadow: "0px 7px 20px rgba(0, 0, 0, 0.15)",
  marginBottom: "20px",
});

const ColorItem = styled<{ $background?: string; $inverse?: boolean }, "div">(
  "div",
  ({ $background, $inverse }) => ({
    alignItems: "center",
    color: $inverse ? "#FFF" : "#000",
    display: "flex",
    height: "40px",
    justifyContent: "space-between",
    padding: "8px 16px",
    background: $background,
  })
);

const Label = styled("label", {
  fontSize: "12px",
});

const Value = styled("label", {
  fontSize: "12px",
  maxWidth: "100px",
  overflow: "hidden",
  textOverflow: "ellipsis",
  whiteSpace: "nowrap",
});

interface Color {
  name: string;
  nameAlias?: string[];
  inverse?: boolean;
}

interface ColorStackProps {
  title: string;
  items: Color[];
}

const ColorSwatch: FunctionComponent<ColorStackProps> = ({ items, title }) => (
  <div>
    <h3>{title}</h3>

    <Wrapper>
      {items.map(({ inverse, name, nameAlias = [] }) => {
        const value = colors?.[name];
        return (
          <ColorItem $background={value} $inverse={inverse} key={name}>
            <Label>
              {name}
              {nameAlias.map((name, index) => (
                <div key={name} style={{ fontSize: "10px" }}>
                  {name}
                  {index > 0 && <br />}
                </div>
              ))}
            </Label>

            {<Value>{value}</Value>}
          </ColorItem>
        );
      })}
    </Wrapper>
  </div>
);

export default ColorSwatch;
