import React, { FunctionComponent } from "react";
import {
  AvatarOverrides,
  Avatar as BaseWebAvatar,
  StyleProps,
} from "baseui/avatar";
import deepmerge from "deepmerge";
import { useStyletron } from "../baseWeb";

interface AvatarProps {
  name: string;
  size?: string;
  src?: string;
  styleOverrides?: any;
}

/**
 * `Avatar` component.
 *
 * Demo:
 * - [Avatar](https://docs.blockfi.com/storybook/index.html?path=/docs/baseweb-content-avatar--default-story)
 *
 */
const Avatar: FunctionComponent<AvatarProps> = ({
  name,
  size,
  src,
  styleOverrides,
}) => {
  const [, theme] = useStyletron();
  const { avatarBackground, avatarText } = theme.colors;

  return (
    <BaseWebAvatar
      name={name}
      overrides={deepmerge(
        {
          Root: {
            style: {
              backgroundColor: avatarBackground,
              fontFamily: "Public Sans",
            },
          },
          Initials: {
            style: {
              fontWeight: "bold",
              fontSize: "14px",
              color: avatarText,
            },
          },
        },
        styleOverrides || {}
      )}
      size={size || theme.sizing.scale1000}
      src={src}
    />
  );
};

export { Avatar };
