import React, { forwardRef } from "react";
import { colorMode } from "../theme/colors";
import { CircleProgressBarBase } from "./Styled";

export interface CircleBarProps {
  minValue: number;
  maxValue: number;
  value?: number;
  showValue?: boolean;
  errorMessage?: string | ReactElement;
  title: string;
  themeColor: ThemeColor;
  $percent?: number;
}

export const CircleProgressBar = forwardRef(
  (
    { value = 0, ...props }: CircleBarProps,
    ref: React.ForwardedRef<HTMLDivElement>
  ) => {
    return (
      <CircleProgressBarBase
        $percent={100 - value}
        {...{ ref, ...props }}
        $colorMode={colorMode[themeColor]}
      >
        <svg
          className="circleContainer"
          viewBox="2 -2 28 36"
          xmlns="http://www.w3.org/2000/svg"
        >
          <circle
            className="circleContainerBackground"
            r="16"
            cx="16"
            cy="16"
          ></circle>
          <circle className="circleOffset" r="16" cx="16" cy="16"></circle>
        </svg>
      </CircleProgressBarBase>
    );
  }
);
