import React, { FC } from "react";
import styled from "styled-components";
import { colors, ThemeColor } from "../../theme/colors";
import { SparkLine } from "../../charts/sparkline";
import { CryptoSymbols } from "../../static/types";
import { CryptoIcon } from "../../icon/icon";

export interface HighlightCardProps {
  width?: string;
  height?: string;
  color: ThemeColor;
  chartData: number[];
  chartType: "bar" | "line";
  title?: string;
  amount?: string;
  currency?: CryptoSymbols;
}

const HighlightCard: FC<HighlightCardProps> = ({
  width = "400px",
  height = "150px",
  color = "blue",
  chartType = "line",
  chartData = [],
  title,
  amount,
  currency,
}) => {
  return (
    <Container
      $height={height}
      $width={width}
      $backgroundColor={colors[color].background}
      $color={colors[color].color}
    >
      <Details>
        <div className="title">{title}</div>
        <div className="amount">
          {currency && <CryptoIcon cryptoSymbol={currency} />}
          <div style={{ marginLeft: "10px" }}>{amount}</div>
        </div>

        <div className="more"></div>
      </Details>

      <SparkLine
        chartType={chartType}
        chartData={chartData}
        width="50%"
        height="50%"
      />
    </Container>
  );
};

export default HighlightCard;

interface CardProps {
  $width?: string;
  $height?: string;
  $backgroundColor: string;
  $color: string;
}

const Container = styled.div<CardProps>`
  display: flex;
  flex-direction: row;
  justify-content: space-between;
  align-items: center;
  color: ${({ $color }) => $color};
  width: ${({ $width }) => $width};
  height: ${({ $height }) => $height};
  background-color: ${({ $backgroundColor }) => $backgroundColor};
  border-radius: 15px;
  padding: 25px;

  .title {
    font-size: 20px;
    color: ${({ $color }) => $color};
  }
  .amount {
    display: flex;
    flex-direction: row;
    align-items: center;
    font-size: 32px;
    font-weight: bold;
  }
`;

const Details = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: space-between;
  height: 100%;
`;
