import { VegaBorders } from "./types";

const defaultBorderStyle = "solid";
const width100 = "1px";
const width200 = "2px";

const borders: VegaBorders = {
  borderWidth100: {
    borderTopStyle: defaultBorderStyle,
    borderRightStyle: defaultBorderStyle,
    borderBottomStyle: defaultBorderStyle,
    borderLeftStyle: defaultBorderStyle,
    borderTopWidth: width100,
    borderRightWidth: width100,
    borderBottomWidth: width100,
    borderLeftWidth: width100,
  },
  borderWidth200: {
    borderTopStyle: defaultBorderStyle,
    borderRightStyle: defaultBorderStyle,
    borderBottomStyle: defaultBorderStyle,
    borderLeftStyle: defaultBorderStyle,
    borderTopWidth: width200,
    borderRightWidth: width200,
    borderBottomWidth: width200,
    borderLeftWidth: width200,
  },
};

export default borders;
